import { useCallback, useEffect, useState } from "react";
import { useCustomFetch } from "src/hooks/useCustomFetch";
import { SetTransactionApprovalParams } from "src/utils/types";
import { TransactionPane } from "./TransactionPane";
import { SetTransactionApprovalFunction, TransactionsComponent } from "./types";

export const Transactions: TransactionsComponent = ({ transactions }) => {
  const { fetchWithoutCache, loading } = useCustomFetch();
  const [transactionApproval, setTransactionApproval] = useState<{ [id: string]: boolean }>({});

  const setTransactionApprovalState = useCallback<SetTransactionApprovalFunction>(
    async ({ transactionId, newValue }) => {
      await fetchWithoutCache<void, SetTransactionApprovalParams>("setTransactionApproval", {
        transactionId,
        value: newValue,
      });

      // Update the local state to reflect the approval status
      setTransactionApproval((prevState) => ({
        ...prevState,
        [transactionId]: newValue,
      }));
    },
    [fetchWithoutCache]
  );

  useEffect(() => {
    if (transactions) {
      // Initialize transaction approval state based on transactions
      const initialApprovalState: { [id: string]: boolean } = {};
      transactions.forEach(transaction => {
        initialApprovalState[transaction.id] = transaction.approved; // assuming transactions have 'approved' property
      });
      setTransactionApproval(initialApprovalState);
    }
  }, [transactions]);

  if (transactions === null) {
    return <div className="RampLoading--container">Loading...</div>;
  }

  return (
    <div data-testid="transaction-container">
      {transactions.map((transaction) => (
        <TransactionPane
          key={transaction.id}
          transaction={transaction}
          loading={loading}
          setTransactionApproval={setTransactionApprovalState}
          isApproved={transactionApproval[transaction.id]} // Pass the approval state
        />
      ))}
    </div>
  );
};
